import React, { useState, useEffect } from "react";

import { Form, Button } from "react-bootstrap";
import { withRouter, Link, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { signin } from "../../redux/action";
import adminLogin from "../../admin/adminLogin";
import './login.css'

const Signin = (props) => {
  const { signin, signinResponse } = props;
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const history = useHistory("");

  useEffect(() => {
    if (signinResponse) {
      history.push("homepage");
    } else {
      alert('Sorry, Email or Password is Wrong')
    }
  }, [signinResponse]);
  return (
    <div id="form">
        <Form id="form-login"className="m-4">
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              placeholder="Your Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Your Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Group>

          <Button
            variant="primary"
            onClick={() => signin({ username: username, password: password })}
          >
            Sign in
          </Button>
          <br />
          Don't have an account? please, <Link to="/signup">Sign up</Link>
        </Form>
    </div>
    );
};

const mapStateToProps = (state) => {
  return {
    signinResponse: state.bookReducer.signinResponse,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    signin: (data) => dispatch(signin(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Signin));
