import React from 'react'
// import Button from '../../components/button/buttonAddToCart'
import { connect } from "react-redux";
import {Table, Button} from 'react-bootstrap'
import numeral from "numeral";
import './cart.css'


const Cart = (props) => {
  console.log(props)

  const totalAllProduct = props.bookInCart.reduce((a, b) => a + (b.price), 0)
  const handleAjustQty = (type, id) => {
    const findProduct = props.bookInCart && props.bookInCart.find((val) => val.id === id)
    const qty = type === '+' ? findProduct.qty + 1 :  findProduct.qty - 1
    findProduct.qty = qty
    findProduct.stock = findProduct.stock_real - qty
    // setDataContext({...dataContext})
  }

  // const DeleteListCart = ()

  return (
    <div>
      <h2>Daftar Keranjang</h2>
      <h3>saldo kamu : {`Rp ${numeral(props.signedUser[0].saldo).format("0,0")}`}</h3>

            <div id="cartList">
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>No</th>
                    <th>Product Name</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                {props.bookInCart && props.bookInCart.map((val, key) => {
                  console.log(val)
                    return (
                      <div>
                        <tr key={key}>
                          <td>{key + 1}</td>
                          <td>{val.title}</td>
                          <td>
                          <button onClick={() => handleAjustQty('-', val.id)}>-</button>
                            <input 
                              // value={val.qty} 
                              // onChange={(e) => handleChangeQty(e, val.id)}
                            />
                          <button onClick={() => handleAjustQty('+', val.id)}>+</button>
                          </td>
                          <td>{`Rp ${numeral(val.price).format("0,0")}`}</td>
                          <td></td>
                        </tr>
                        <Button variant="danger">Danger</Button>
                      </div>
                    )
                })}
                </tbody>
              </Table>
            </div>

            <h2>Total Keranjang : {props.bookInCart.length}</h2>


          <div id="cartSummary">
            <h2>Total : {`Rp ${numeral(totalAllProduct).format("0,0")}`}</h2>
            <Button variant="warning">Checkout</Button>
          </div>


    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    books: state.bookReducer.books,
    bookInCart: state.bookReducer.booksInCart,
    signedUser : state.bookReducer.signedUser
  };
};

export default connect(mapStateToProps)(Cart)
