import React, { useState, useEffect, useContext} from "react";
import { Card, Button, FormControl, Form } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { withRouter } from "react-router-dom";
import numeral from "numeral";
import {DataContext} from '../../context/DataContext'

import { connect } from "react-redux";
// import { addToCart } from "../../redux/action/globalActionType"
import "./Product.css"

const Book = (props) => {
  const { book } = props;
  const [data, setData] = useState({});

  useEffect(() => {
    setData({
      ...book,
      bookCategory: { ...book.bookCategory },
      id: book.id,
      title: book.title,
      synopsis: book.synopsis,
      price: book.price,
      bookStatus: book.bookStatus,
      authorName: book.authorName,
      publicationDate: new Date(),
    });
  }, []);

console.log(book)

const {dataContext, setDataContext} = useContext(DataContext)

const addToCart = id => {
  const carts = dataContext ? dataContext.carts : []
  const findProduct = book.find((val) => val.id === id)
  carts.push({...findProduct, qty: 1})
  setDataContext({
    ...dataContext,
    carts
  })
}
