import React, { useState, useEffect } from "react";

import { Form, Button } from "react-bootstrap";
import { withRouter, Link, useHistory } from "react-router-dom";
import { connect } from "react-redux";
import { signin } from "../../redux/action";
import adminLogin from "../../admin/adminLogin";
import './login.css'

const Signin = (props) => {
  const { signin, signinResponse, signedUser} = props;
  // const [username, setUsername] = useState("");
  // const [password, setPassword] = useState("");
  const [FormData, setFormData] = useState({
    username: '',
    password: '',
  })
  const history = useHistory("");

  useEffect(() => {
    if (signinResponse) {
      history.push("homepage");
    }
  }, [signinResponse]);

  const handleSignUpChange = (event, param) => {
    setFormData({
        ...FormData,
        [param] : event.target.value
    })
    console.log(FormData)
}

  const checkInput = () => {
    if(FormData.username.length === 0 || FormData.password.length === 0) {
      alert('Email dan Password belum terisi')
    } else if(FormData.username === signedUser.username && FormData.password === signedUser.password) {
        props.history.push('/admin/setProduct')
      console.log('log in sesuai')

    //   setIsInformation(true)
  } else {
      alert('email atau password salah')
  }
}


  return (
    <div id="form">
        <Form id="form-login"className="m-4">
          <Form.Group controlId="formBasicEmail">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              placeholder="Your Username"
              value={FormData.username} 
              onChange={(event) => handleSignUpChange(event, 'username')}
            />
          </Form.Group>

          <Form.Group controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              placeholder="Your Password"
              value={FormData.password} 
              onChange={(event) => handleSignUpChange(event, 'password')}
            />
          </Form.Group>

          <Button
            variant="primary"
            onClick={() => signin({ username: FormData.username, password: FormData.password })}
            onClick={checkInput}
          >
            Sign in
          </Button>
          <br />
          Don't have an account? please, <Link to="/signup">Sign up</Link>
        </Form>
    </div>
    );
};

const mapStateToProps = (state) => {
  return {
    signinResponse: state.bookReducer.signinResponse,
    signedUser : state.bookReducer.signedUser
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    signin: (data) => dispatch(signin(data)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Signin));
