import React from 'react'
// import Button from '../../components/button/buttonAddToCart'
import { connect } from "react-redux";


const Cart = (props) => {
  console.log(props)
  return (
    <div>
      <h2>Daftar Keranjang</h2>
      <h2>Total Keranjang : {props.bookInCart.length}</h2>
      <h3>saldo kamu : {props.signedUser[0].saldo}</h3>


            <table width="80%">
              <thead>
                <tr>
                  <th>No</th>
                  <th>Product Name</th>
                  <th>Qty</th>
                  <th>Price</th>
                  <th>Subtotal</th>
                  <th></th>
                </tr>
              </thead>
              <tbody>
              {props.bookInCart && props.bookInCart.map((val, key) => {
                console.log(val)
                  return (
                    <tr key={key}>
                      <td>{key + 1}</td>
                      <td>{val.title}</td>
                      <td>+</td>
                      <td>-</td>
                      <td>{val.price}</td>
                    </tr>
                  )
              })}


              </tbody>
            </table>


          <div>
            <h2>Total :</h2>

          </div>

    </div>
  )
}

const mapStateToProps = (state) => {
  return {
    books: state.bookReducer.books,
    bookInCart: state.bookReducer.booksInCart,
    signedUser : state.bookReducer.signedUser
  };
};

export default connect(mapStateToProps)(Cart)
