import React, {useState} from 'react'
import { Link } from 'react-router-dom'

const LogIn = () => {
    let db = {
        email: 'arnov.julian17@gmail.com',
        password: '1234asdf',
    }

    const [formData, setFormData] = useState({
        email: '',
        password: '',
      })
    const [isInformation, setisInformation] = useState(false)

    const handleSignUpChange = (event, param) => {
          setFormData({
              ...formData,
              [param] : event.target.value
          })
          console.log(formData)
          checkInput()
      }

      const checkInput = (event) => {
        if(formData.email.length === 0 || formData.password.length === 0) {
        //   alert('Email dan Password belum terisi')
        } else if(formData.email === db.email && formData.password === db.password) {
          console.log('log in sesuai')
          setisInformation(true)
      } else {
        //   alert('email atau password salah')
      }
    }

    return(
        <div>
            <form>
                    <div class="form-group">
                        <label for="inputAddress">Email</label>
                        <input type="email" class="form-control" id="inputEmail" placeholder="Your Email"
                        value={formData.email} onChange={(event) => handleSignUpChange(event, 'email')}/>
                    </div>
                    <div class="form-group">
                        <label for="inputAddress2">Password</label>
                        <input type="password" class="form-control" id="inputPassword" placeholder="Your Password"
                        value={formData.password} onChange={(event) => handleSignUpChange(event, 'password')}/>
                    </div>
                <button type="submit" class="btn btn-primary" isInformation={isInformation}>Log in</button>
                <button type="submit" class="btn btn-warning"><Link to='/register'>Register</Link></button>

            </form>
        </div>
    )
}

export default LogIn
