import * as actionTypes from "../action/actionTypes";

const initialState = {
  users : [{username : 'arnov', password : 'arnov'}],
  signInRespone : false,
  signInUser : {},
  books: [],
  book: {},
  booksInCart: [],
};

const book = (state = initialState, action) => {
  // console.log("action", action);
  switch (action.type) {
    case actionTypes.SIGNUP:
      return {
        ...state,
        users : [...state.users, action.payload]
      }
    case actionTypes.SIGNIN:
      const findUser = state.users.find(
        (user) => 
        user.username === action.payload.username && user.password === action.payload.password
      ) ? true : false ;

      const filterUser = state.users.filter(
        (user) => 
        user.username === action.payload.username && user.password === action.payload.password 
      );
      return {
        ...state,
        signInRespone : findUser,
        signInUser : filterUser,
      };

    case actionTypes.SIGNOUT:
      return {
        ...state,
        signInRespone : false,
      };

    case actionTypes.GET_BOOK:
      return {
        ...state,
        books: action.payload,
      };
    case actionTypes.GET_BOOK_BY_ID:
      return {
        ...state,
        book: action.payload,
      };
    case actionTypes.UPDATE_BOOK:
      return {
        ...state,
      };
    case actionTypes.DELETE_BOOK:
      return {
        ...state,
      };
    case actionTypes.ADD_TO_CART:
      // console.log("action.payload", action.payload);
      return {
        ...state,
        booksInCart: [...state.booksInCart, action.payload],
        //booksInCart: state.booksInCart.push(action.payload),
      };
    default:
      return initialState;
  }
};

export default book;
